","keccak256":"0x2526b7c6a366fc211bbc176078ab02b83833e7682abcc5b3bed9d700b34e2a3e","license":"MIT"},"contracts/base/LogicVersionsRegistry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Commons and Libraries
import "@openzeppelin/contracts/utils/Address.sol";
import "../util/AddressLib.sol";
import "../util/LogicVersionLib.sol";
import "../util/TellerCommon.sol";

// Interfaces
import "../interfaces/LogicVersionsRegistryInterface.sol";
import "../interfaces/IBaseProxy.sol";
import "../interfaces/SettingsInterface.sol";

/**
    @notice It manages all the logic contract versions, mapping each one to a logic name (or key).
    @author develop@teller.finance
 */
contract LogicVersionsRegistry is LogicVersionsRegistryInterface {
    using LogicVersionLib for LogicVersionLib.LogicVersion;
    using Address for address;

    /* State Variables */

    /**
     * @notice It is the only address that may make changes in the contract.
     */
    address public owner;

    /**
        @dev It represents a mapping to identify a logic name (key) and the current logic address and version.

        i.e.:
            bytes32("EtherCollateralLoans") => { logic: "0x123...789", version: 1 }.
            bytes32("LendingPool") => { logic: "0x456...987", version: 3 }.
     */
    mapping(bytes32 => LogicVersionLib.LogicVersion) internal logicVersions;

    /** Modifiers */

    /**
     * @notice It checks that the sender is the owner address.
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "NOT_OWNER");
        _;
    }

    /** External Functions */
    /**
        @notice It creates multiple logic versions.
        @param newLogicVersions lists of the new logic versions to create.
     */
    function createLogicVersions(
        TellerCommon.CreateLogicVersionRequest[] calldata newLogicVersions
    ) external override onlyOwner {
        _createLogicVersions(newLogicVersions);
    }

    /**
        @notice It upgrades multiple logic addresses.
        @param newLogicVersions lists of the new logic versions to create.
     */
    function upgradeLogicVersions(
        TellerCommon.UpgradeLogicVersionRequest[] calldata newLogicVersions
    ) external override onlyOwner {
        _upgradeLogicVersions(newLogicVersions);
    }

    /**
        @notice It upgrades a logic version given a logic name.
        @param logicName logic name to upgrade.
        @param newLogic the new logic address to set.
        @param proxy The (optional) DynamicUpgradeable proxy address to attempt to directly upgrade.
     */
    function upgradeLogicVersion(
        bytes32 logicName,
        address newLogic,
        address proxy
    ) external override onlyOwner {
        _upgradeLogicVersion(logicName, newLogic, proxy);
    }

    /**
        @notice It rollbacks a logic to a previous version.
        @param logicName logic name to rollback.
        @param previousVersion the previous version to be used.
     */
    function rollbackLogicVersion(bytes32 logicName, uint256 previousVersion)
        external
        override
        onlyOwner
    {
        (uint256 currentVersion, address previousLogic, address newLogic) =
            logicVersions[logicName].rollback(previousVersion);

        emit LogicVersionRollbacked(
            logicName,
            previousLogic,
            newLogic,
            currentVersion,
            previousVersion
        );
    }

    /**
        @notice Transfers ownership of the contract to a new account (`newOwner`).
        @dev Can only be called by the current owner.
        @param newOwner The address that should be the new owner.
     */
    function transferOwnership(address newOwner) public onlyOwner {
        owner = newOwner;
    }

    /**
        @notice It gets the current logic version for a given logic name.
        @param logicName to get.
        @return currentVersion the current logic version number.
        @return latestVersion the latest version number (higher than current if rolled back).
        @return logic the address of the current logic version
     */
    function getLogicVersion(bytes32 logicName)
        external
        view
        override
        returns (
            uint256 currentVersion,
            uint256 latestVersion,
            address logic
        )
    {
        LogicVersionLib.LogicVersion storage logicVersion =
            logicVersions[logicName];

        currentVersion = logicVersion.currentVersion;
        latestVersion = logicVersion.latestVersion;
        logic = logicVersion.versions[currentVersion];
    }

    /**
        @notice It tests whether a logic name is already configured.
        @param logicName logic name to test.
        @return true if the logic version is already configured. Otherwise it returns false.
     */
    function hasLogicVersion(bytes32 logicName)
        external
        view
        override
        returns (bool)
    {
        return logicVersions[logicName].exists;
    }

    /**
        @notice It initializes this logic versions constants.
        @param aOwner address of the owner of the registry.
        @param initialLogicVersions lists of the new logic versions to create.
     */
    function initialize(
        address aOwner,
        TellerCommon.CreateLogicVersionRequest[] calldata initialLogicVersions
    ) external override {
        require(owner == address(0), "ALREADY_INIT");
        owner = aOwner;
        _createLogicVersions(initialLogicVersions);
    }

    /** Internal functions */

    /**
        @notice It creates a new logic version given a logic name and address.
        @param logicName logic name to create.
        @param logic the logic address value for the given logic name.
     */
    function _createLogicVersion(bytes32 logicName, address logic) internal {
        require(logicName != "", "LOGIC_NAME_MUST_BE_PROVIDED");
        logicVersions[logicName].initialize(logic);

        emit LogicVersionCreated(logicName, logic, 0);
    }

    /**
        @notice It creates multiple logic versions.
        @param newLogicVersions lists of the logic versions to create.
     */
    function _createLogicVersions(
        TellerCommon.CreateLogicVersionRequest[] memory newLogicVersions
    ) internal {
        for (uint256 i; i < newLogicVersions.length; i++) {
            _createLogicVersion(
                newLogicVersions[i].logicName,
                newLogicVersions[i].logic
            );
        }
    }

    /**
        @notice It upgrades a new logic version given a logic name and address.
        @param logicName logic name to create.
        @param newLogic the logic address value for the given logic name.
        @param proxy The (optional) DynamicUpgradeable proxy address to attempt to directly upgrade.
     */
    function _upgradeLogicVersion(
        bytes32 logicName,
        address newLogic,
        address proxy
    ) internal {
        // If proxy given, make sure its not strict dynamic and logic names match before directly upgrading.
        if (proxy != address(0)) {
            (bool success, bytes memory data) =
                proxy.staticcall(abi.encodeWithSignature("strictDynamic()"));
            require(
                success && abi.decode(data, (bool)),
                "PROXY_STRICT_DYNAMIC"
            );

            (success, data) = proxy.staticcall(
                abi.encodeWithSignature("logicName()")
            );
            require(
                success && abi.decode(data, (bytes32)) == logicName,
                "MISMATCH_LOGIC_NAME"
            );

            (success, ) = proxy.call(
                abi.encodeWithSignature("upgradeProxyTo(address)", newLogic)
            );
            require(success, "FAILED_TO_DIRECTLY_UPGRADE");
        }

        (address oldLogic, uint256 oldVersion, uint256 newVersion) =
            logicVersions[logicName].upgrade(newLogic);

        emit LogicVersionUpgraded(
            logicName,
            oldLogic,
            newLogic,
            oldVersion,
            newVersion
        );
    }

    /**
        @notice It upgrades multiple logic versions.
        @param newLogicVersions Lists of the logic versions to upgrade.
     */
    function _upgradeLogicVersions(
        TellerCommon.UpgradeLogicVersionRequest[] memory newLogicVersions
    ) internal {
        for (uint256 i; i < newLogicVersions.length; i++) {
            _upgradeLogicVersion(
                newLogicVersions[i].logicName,
                newLogicVersions[i].logic,
                newLogicVersions[i].proxy
            );
        }
    }
}
","keccak256":"0xaaa396761988a3994a17b17a05e7efc91eaf62031237b4b57a9f42867d268185","license":"MIT"},"contracts/base/proxies/BaseDynamicProxy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Contracts
import "./BaseProxy.sol";
import "../upgradeable/DynamicUpgradeable.sol";

/**
    @notice It is a dynamic proxy contract for any contract. It uses the logic versions registry to get a logic contract address.
    @notice It extends BaseUpgradeable to get access to the settings.

    @author develop@teller.finance
 */
abstract contract BaseDynamicProxy is BaseProxy, DynamicUpgradeable {
    function _implementation()
        internal
        view
        virtual
        override(DynamicUpgradeable, Proxy)
        returns (address)
    {
        return DynamicUpgradeable._implementation();
    }

    /**
     * @notice It is called by the OZ proxy contract before calling the internal _implementation() function.
     */
    function _beforeFallback() internal override {
        if (strictDynamic && _implementationBlockUpdated + 50 <= block.number) {
            address(this).delegatecall(
                abi.encodeWithSignature("_updateImplementationStored()")
            );
        }
    }
}
","keccak256":"0x2df52440edf97dd9fb3e60220e6fa64446ba3b07cfa1b822bb5f42f69a538965","license":"MIT"},"contracts/base/proxies/BaseProxy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Interfaces
import "../../interfaces/IBaseProxy.sol";

// Contracts
import "@openzeppelin/contracts/proxy/Proxy.sol";

/**
    @notice It is the base Proxy contract for all other Proxy contracts.
    @dev It makes the current logic implementation address publicly available.

    @author develop@teller.finance
 */
abstract contract BaseProxy is IBaseProxy, Proxy {
    /**
        @notice Returns the current implementation.
        @return Address of the current implementation
     */
    function implementation() external view override returns (address) {
        return _implementation();
    }
}
","keccak256":"0xffd928e3bea6e29964581b553e5566049086e9faed792cc264197ca7644fac5c","license":"MIT"},"contracts/base/proxies/SettingsDynamicProxy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Contracts
import "./BaseDynamicProxy.sol";
import "../LogicVersionsRegistry.sol";

/**
    @notice It is a dynamic proxy contract for any contract. It uses the logic versions registry to get a logic contract address.
    @notice It extends BaseUpgradeable to get access to the settings.

    @author develop@teller.finance
 */
contract SettingsDynamicProxy is BaseDynamicProxy {
    /**
        @notice It creates the LogicVersionsRegistry that will be used for the platform and create initial logic versions.
        @param initialLogicVersions list of the logic versions to create.
     */
    constructor(
        TellerCommon.CreateLogicVersionRequest[] memory initialLogicVersions
    ) {
        logicRegistry = new LogicVersionsRegistry();
        logicRegistry.initialize(msg.sender, initialLogicVersions);

        logicName = keccak256("Settings");
        strictDynamic = false;
        _updateImplementationStored();
    }
}
","keccak256":"0x264a656d298d3918d5c117ada45bbc9aa801dba2df7bab92a3faf2e1aaa3b979","license":"MIT"},"contracts/base/upgradeable/DynamicUpgradeable.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Contracts
import "./DynamicUpgradeableStorage.sol";

/*****************************************************************************************************/
/**                                             WARNING                                             **/
/**                              THIS CONTRACT IS AN UPGRADEABLE FACET!                             **/
/**  ---------------------------------------------------------------------------------------------  **/
/**  Do NOT place ANY storage/state variables directly in this contract! If you wish to make        **/
/**  make changes to the state variables used by this contract, do so in its defined Storage        **/
/**  contract that this contract inherits from                                                      **/
/**                                                                                                 **/
/**  Visit https://docs.openzeppelin.com/upgrades/2.6/proxies#upgrading-via-the-proxy-pattern for   **/
/**  more information.                                                                              **/
/*****************************************************************************************************/
/**
 * @notice This contract is used define the DynamicUpgradeable contracts logic.
 *
 * @author develop@teller.finance
 */
contract DynamicUpgradeable is DynamicUpgradeableStorage {
    /* Modifiers */

    /**
     * @notice It checks if the proxy's implementation cache is invalidated and should be updated.
     * @dev Any external, non-view function should use this modifier.
     * @dev This modifier should be the very FIRST modifier for functions.
     */
    modifier updateImpIfNeeded() {
        if (_cacheInvalidated()) {
            _updateImplementationStored();
        }
        _;
    }

    /* External Functions */

    /**
     * @notice It updates a proxy's cached implementation address.
     * @notice It must only be called by the LogicVersionsRegistry for non strict DynamicProxy
     */
    function upgradeProxyTo(address newImplementation) public {
        require(msg.sender == address(logicRegistry), "MUST_BE_LOGIC_REGISTRY");
        implementationStored = newImplementation;
        _implementationBlockUpdated = block.number;
    }

    /* Internal Functions **/

    /**
     * @notice Returns the current implementation used by the proxy to delegate a call to.
     * @return address of the current implementation
     */
    function _implementation() internal view virtual returns (address) {
        if (_cacheInvalidated()) {
            (, , address currentLogic) =
                logicRegistry.getLogicVersion(logicName);
            return currentLogic;
        }
        return implementationStored;
    }

    /**
     * @notice Updates the current implementation logic address for the stored logic name.
     * @dev It uses the LogicVersionsRegistry contract to get the logic address or the cached address if valid.
     * @dev It caches the current logic address for the proxy to reduce gas on subsequent calls within the same block.
     */
    function _updateImplementationStored() public {
        (, , address currentLogic) = logicRegistry.getLogicVersion(logicName);

        if (implementationStored != currentLogic) {
            implementationStored = currentLogic;
        }
        _implementationBlockUpdated = block.number;
    }

    /**
     * @notice It checks if the current cached address implementation is marked as invalidated.
     * @notice It is marked invalidated if the proxy is strict dynamic and last update was >= 50 blocks ago.
     * @return bool True if the cached implementation address is invalid.
     */
    function _cacheInvalidated() internal view returns (bool) {
        return strictDynamic && _implementationBlockUpdated + 1 <= block.number;
    }
}
