pragma solidity ^0.8.0;

// Commons
import "../../util/TellerCommon.sol";

// Interfaces
import "../SettingsInterface.sol";
import "./ILoanData.sol";

/**
 * @notice This interface defines the functions to work with the Teller loans protocol
 *
 * @author develop@teller.finance
 */
interface ILoanManager {
    /**
     * @notice Deposit collateral for a loan, unless it isn't allowed
     * @param borrower Account address of the borrower
     * @param loanID ID of loan from which collateral was withdrawn
     * @param amount Amount to be deposited as collateral
     */
    function depositCollateral(
        address borrower,
        uint256 loanID,
        uint256 amount
    ) external payable;

    /**
     * @notice Withdraw collateral from a loan, unless this isn't allowed
     * @param amount The amount of collateral token or ether the caller is hoping to withdraw
     * @param loanID The ID of the loan the collateral is for
     */
    function withdrawCollateral(uint256 amount, uint256 loanID) external;

    /**
     * @notice Create a loan with specified terms, if allowed
     * @param request Struct of the protocol loan request
     * @param responses List of structs of the protocol loan responses
     * @param collateralAmount Amount of collateral for the loan
     */
    function createLoanWithTerms(
        TellerCommon.LoanRequest calldata request,
        TellerCommon.LoanResponse[] calldata responses,
        uint256 collateralAmount
    ) external payable;

    /**
     * @notice Take out a loan, if allowed
     * @param loanID The ID of the loan to be taken out
     * @param amountBorrow Amount of tokens to be taken out in the loan
     */
    function takeOutLoan(uint256 loanID, uint256 amountBorrow) external;

    /**
     * @notice Make a payment to a specified loan
     * @param amount The amount of tokens to pay back to the loan
     * @param loanID The ID of the loan the payment is for
     */
    function repay(uint256 amount, uint256 loanID) external;

    /**
     * @notice Liquidate a loan if has is expired or under collateralized
     * @param loanID The ID of the loan to be liquidated
     */
    function liquidateLoan(uint256 loanID) external;

    /**
        @notice It adds a new account as a signer.
        @param account address to add.
        @dev The sender must be the owner.
        @dev It throws a require error if the sender is not the owner.
     */
    function addSigner(address account) external;

    /**
        @notice It adds a list of account as signers.
        @param accounts addresses to add.
        @dev The sender must be the owner.
        @dev It throws a require error if the sender is not the owner.
     */
    function addSigners(address[] calldata accounts) external;

    /**
     *  @notice It calls the LogicVersionRegistry to update the stored logic address for LoanData.
     */
    function updateLoanDataLogic() external;

    /**
     *  @notice It calls the LogicVersionRegistry to update the stored logic address for LoanTermsConsensus.
     */
    function updateLoanTermsConsensusLogic() external;

    /**
     * @notice Initializes the current contract instance setting the required parameters, if allowed
     * @param lendingPoolAddress Contract address of the lending pool
     * @param settingsAddress Contract address for the configuration of the platform
     * @param collateralTokenAddress Contract address for the collateral token
     * @param initDynamicProxyLogicAddress Address of a deployed InitializableDynamicProxy contract.
     */
    function initialize(
        address lendingPoolAddress,
        address settingsAddress,
        address collateralTokenAddress,
        address initDynamicProxyLogicAddress
    ) external;

    /**
     * @notice This event is emitted when collateral has been deposited for the loan
     * @param loanID ID of the loan for which collateral was deposited
     * @param borrower Account address of the borrower
     * @param depositAmount Amount of collateral deposited
     */
    event CollateralDeposited(
        uint256 indexed loanID,
        address indexed borrower,
        uint256 depositAmount
    );

    /**
     * @notice This event is emitted when collateral has been withdrawn
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param recipient Account address of the recipient
     * @param amount Value of collateral withdrawn
     */
    event CollateralWithdrawn(
        uint256 indexed loanID,
        address indexed borrower,
        address indexed recipient,
        uint256 amount
    );

    /**
     * @notice This event is emitted when loan terms have been successfully set
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param recipient Account address of the recipient
     */
    event LoanTermsSet(
        uint256 indexed loanID,
        address indexed borrower,
        address indexed recipient,
        uint256 nonce
    );

    /**
     * @notice This event is emitted when a loan has been successfully taken out
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param escrow Escrow address associated to this loan
     * @param amountBorrowed Total amount taken out in the loan
     */
    event LoanTakenOut(
        uint256 indexed loanID,
        address indexed borrower,
        address indexed escrow,
        uint256 amountBorrowed
    );

    /**
     * @notice This event is emitted when a loan has been successfully repaid
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param amountPaid Amount of the loan paid back
     * @param payer Account address of the payer
     * @param totalOwed Total amount of the loan to be repaid
     */
    event LoanRepaid(
        uint256 indexed loanID,
        address indexed borrower,
        uint256 amountPaid,
        address payer,
        uint256 totalOwed
    );

    /**
     * @notice This event is emitted when a loan has been successfully liquidated
     * @param loanID ID of loan from which collateral was withdrawn
     * @param borrower Account address of the borrower
     * @param liquidator Account address of the liquidator
     * @param collateralOut Collateral that is sent to the liquidator
     * @param tokensIn Percentage of the collateral price paid by the liquidator to the lending pool
     */
    event LoanLiquidated(
        uint256 indexed loanID,
        address indexed borrower,
        address liquidator,
        int256 collateralOut,
        uint256 tokensIn
    );
}
","keccak256":"0x5e1da908dc97209c32fe782260e8101f025e822fed0f5dda6d93554f28272df7","license":"MIT"},"contracts/interfaces/loans/ILoanStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Commons
import "../../util/TellerCommon.sol";

// Interfaces
import "../SettingsInterface.sol";
import "../LendingPoolInterface.sol";
import "./ILoanTermsConsensus.sol";
import "../../providers/compound/CErc20Interface.sol";

/**
    Getting stack too deep issues and cannot get the proper
    struct as a return value from calling loans() externally.
    So we are just providing helpers here to get individual
    fields on a storage pointer of the loan by loanId.
 */
abstract contract ALoanStorage {
    /**
     * @notice Holds the data of all loans for the lending token.
     */
    mapping(uint256 => TellerCommon.Loan) public loans;

    function loanTerms(uint256 loanId)
        public
        view
        returns (TellerCommon.LoanTerms memory)
    {
        return loan(loanId).loanTerms;
    }

    function termsExpiry(uint256 loanId) public view returns (uint256) {
        return loan(loanId).termsExpiry;
    }

    function loanStartTime(uint256 loanId) public view returns (uint256) {
        return loan(loanId).loanStartTime;
    }

    function collateral(uint256 loanId) public view returns (uint256) {
        return loan(loanId).collateral;
    }

    function lastCollateralIn(uint256 loanId) public view returns (uint256) {
        return loan(loanId).lastCollateralIn;
    }

    function principalOwed(uint256 loanId) public view returns (uint256) {
        return loan(loanId).principalOwed;
    }

    function interestOwed(uint256 loanId) public view returns (uint256) {
        return loan(loanId).interestOwed;
    }

    function borrowedAmount(uint256 loanId) public view returns (uint256) {
        return loan(loanId).borrowedAmount;
    }

    function escrow(uint256 loanId) public view returns (address) {
        return loan(loanId).escrow;
    }

    function status(uint256 loanId)
        public
        view
        returns (TellerCommon.LoanStatus)
    {
        return loan(loanId).status;
    }

    function liquidated(uint256 loanId) public view returns (bool) {
        return loan(loanId).liquidated;
    }

    function loan(uint256 loanId)
        internal
        view
        returns (TellerCommon.Loan storage loan_)
    {
        loan_ = loans[loanId];
    }
}

/**
 * @notice This interface defines the functions to get and calculate information about loan data.
 *
 * @author develop@teller.finance
 */
interface ILoanStorage {
    /**
     * @notice Holds the total amount of collateral held by the contract.
     */
    function totalCollateral() external view returns (uint256);

    /**
     * @notice Holds the instance of the LendingPool used by the LoanManager.
     */
    function lendingPool() external view returns (LendingPoolInterface);

    /**
     * @notice Holds the lending token used for creating loans by the LoanManager and LendingPool.
     */
    function lendingToken() external view returns (address);

    /**
     * @notice Holds the collateral token.
     */
    function collateralToken() external view returns (address);

    /**
     * @notice Holds the Compound cToken where the underlying token matches the lending token.
     */
    function cToken() external view returns (CErc20Interface);

    /**
     * @notice Holds the ID of loans taken out
     * @dev Also the next available loan ID
     */
    function loanIDCounter() external view returns (uint256);
}
