pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Address.sol";
import "./AddressLib.sol";

/**
    @notice Utility library of inline functions on the Cache struct.

    @author develop@teller.finance
 */
library CacheLib {
    using AddressLib for address;
    using Address for address;

    enum CacheType { Address, Uint, Int, Byte, Bool }

    /**
        @notice This struct manages the cache of the library instance.
        @param addresses A mapping of address values mapped to cache keys in bytes.
        @param uints A mapping of uint values mapped to cache keys names in bytes.
        @param ints A mapping of int values mapped to cache keys names in bytes.
        @param bites A mapping of bytes values mapped to cache keys names in bytes.
        @param bools A mapping of bool values mapped to cache keys names in bytes.
     */

    struct Cache {
        // Mapping of cache keys names to address values.
        mapping(bytes32 => address) addresses;
        // Mapping of cache keys names to uint256 values.
        mapping(bytes32 => uint256) uints;
        // Mapping of cache keys names to int256 values.
        mapping(bytes32 => int256) ints;
        // Mapping of cache keys names to bytes32 values.
        mapping(bytes32 => bytes32) bites;
        // Mapping of cache keys names to bool values.
        mapping(bytes32 => bool) bools;
    }

    /**
        @notice The constant for the initialization check
     */
    //   bytes32 private constant INITIALIZED = keccak256("Initialized");

    /**
        @notice Initializes the cache instance.
        @param self The current cache
     */
    function initialize(Cache storage self) internal {
        requireNotExists(self);
        self.bools[keccak256("Initialized")] = true;
    }

    /**
        @notice Checks whether the current cache does not, throwing an error if it does.
        @param self The current cache
     */
    function requireNotExists(Cache storage self) internal view {
        require(!exists(self), "CACHE_ALREADY_EXISTS");
    }

    /**
        @notice Checks whether the current cache exists, throwing an error if the cache does not.
        @param self The current cache
     */
    function requireExists(Cache storage self) internal view {
        require(exists(self), "CACHE_DOES_NOT_EXIST");
    }

    /**
        @notice Tests whether the current cache exists or not.
        @param self The current cache.
        @return bool True if the cache exists.
     */
    function exists(Cache storage self) internal view returns (bool) {
        return self.bools[keccak256("Initialized")];
    }

    /**
        @notice Updates the address value for a given key name.
        @param self The current cache
        @param key The key for which the address value is being updated.
        @param newAddress The new address being set.
     */
    function updateAddress(
        Cache storage self,
        bytes32 key,
        address newAddress
    ) internal {
        requireExists(self);
        require(self.addresses[key] != newAddress, "NEW_ADDRESS_REQUIRED");
        self.addresses[key] = newAddress;
    }

    /**
        @notice Updates the uint value for a given key name.
        @param self The current cache
        @param key The key for which the uint value is being updated.
        @param newValue The new value being set.
     */
    function updateUint(
        Cache storage self,
        bytes32 key,
        uint256 newValue
    ) internal {
        requireExists(self);
        require(self.uints[key] != newValue, "NEW_UINT_REQUIRED");
        self.uints[key] = newValue;
    }

    /**
        @notice Tests whether a given uint value is greater than the current stored value for a given key.
        @param self The current cache
        @param key The key for which the uint value is being updated.
        @param amount The value being tested.
        @return bool True if the value exceed the stored value.
     */
    function exceedsUint(
        Cache storage self,
        bytes32 key,
        uint256 amount
    ) internal view returns (bool) {
        requireExists(self);
        return amount > self.uints[key];
    }

    /**
        @notice Updates the int value for a given key name.
        @param self The current cache
        @param key The key for which the int value is being updated.
        @param newValue The new value being set.
     */
    function updateInt(
        Cache storage self,
        bytes32 key,
        int256 newValue
    ) internal {
        requireExists(self);
        require(self.ints[key] != newValue, "NEW_INT_REQUIRED");
        self.ints[key] = newValue;
    }

    /**
        @notice Updates the bytes value for a given key name.
        @param self The current cache
        @param key The key for which the bytes value is being updated.
        @param newBites The new value being set.
     */
    function updateBites(
        Cache storage self,
        bytes32 key,
        bytes32 newBites
    ) internal {
        requireExists(self);
        require(self.bites[key] != newBites, "NEW_BYTES_REQUIRED");
        self.bites[key] = newBites;
    }

    /**
        @notice Updates the bool value for a given key name.
        @param self The current cache
        @param key The key for which the bool value is being updated.
        @param newBool The new value being set.
     */
    function updateBool(
        Cache storage self,
        bytes32 key,
        bool newBool
    ) internal {
        requireExists(self);
        require(self.bools[key] != newBool, "NEW_BOOLEAN_REQUIRED");
        self.bools[key] = newBool;
    }

    /**
     */
    function clearCache(
        Cache storage self,
        bytes32[5] memory keysToClear,
        CacheType[5] memory keyTypes
    ) internal {
        requireExists(self);
        require(
            keysToClear.length == keyTypes.length,
            "ARRAY_LENGTHS_MISMATCH"
        );
        for (uint256 i; i <= keysToClear.length; i++) {
            if (keyTypes[i] == CacheType.Address) {
                delete self.addresses[keysToClear[i]];
            } else if (keyTypes[i] == CacheType.Uint) {
                delete self.uints[keysToClear[i]];
            } else if (keyTypes[i] == CacheType.Int) {
                delete self.ints[keysToClear[i]];
            } else if (keyTypes[i] == CacheType.Byte) {
                delete self.bites[keysToClear[i]];
            } else if (keyTypes[i] == CacheType.Bool) {
                delete self.bools[keysToClear[i]];
            }
        }
    }
}
","keccak256":"0x73fda00db863d2ff1008716d149b190eda91ef8c9d3e3e2083fa217c50337edc","license":"MIT"},"contracts/util/LogicVersionLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Libraries
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**
    @notice This library manages the functions for the logic version struct.

    @author develop@teller.finance
 */
library LogicVersionLib {
    using SafeMath for uint256;
    using Address for address;

    /**
        @notice It stores all the versions for a given logic.
        @param currentVersion the current version.
        @param latestVersion the latest version.
        @param versions mapping version to logic address.
        @param exists boolean to test whether this logic version exists or not.
     */
    struct LogicVersion {
        uint256 currentVersion;
        uint256 latestVersion;
        mapping(uint256 => address) versions;
        bool exists;
    }

    /**
        @notice It creates a new logic version.
        @param self the current logic version instance.
        @param logic initial logic address.
     */
    function initialize(LogicVersion storage self, address logic) internal {
        requireNotExists(self);
        require(logic.isContract(), "LOGIC_MUST_BE_CONTRACT");
        self.currentVersion = 0;
        self.latestVersion = 0;
        self.versions[self.currentVersion] = logic;
        self.exists = true;
    }

    /**
        @notice It rollbacks a logic to a previous version.
        @param self the current logic version instance.
        @param previousVersion the previous version to be used.
     */
    function rollback(LogicVersion storage self, uint256 previousVersion)
        internal
        returns (
            uint256 currentVersion,
            address previousLogic,
            address newLogic
        )
    {
        requireExists(self);
        require(
            self.currentVersion != previousVersion,
            "CURRENT_VERSION_MUST_BE_DIFF"
        );
        require(
            self.latestVersion >= previousVersion,
            "VERSION_MUST_BE_LTE_LATEST"
        );
        currentVersion = self.currentVersion;
        previousLogic = self.versions[self.currentVersion];
        newLogic = self.versions[previousVersion];

        self.currentVersion = previousVersion;
    }

    /**
        @notice Checks whether the current logic version exists or not.
        @dev It throws a require error if the logic version already exists.
        @param self the current logic version.
     */
    function requireNotExists(LogicVersion storage self) internal view {
        require(!self.exists, "LOGIC_ALREADY_EXISTS");
    }

    /**
        @notice Checks whether the current logic version exists or not.
        @dev It throws a require error if the current logic version doesn't exist.
        @param self the current logic version.
     */
    function requireExists(LogicVersion storage self) internal view {
        require(self.exists, "LOGIC_NOT_EXISTS");
    }

    /**
        @notice It upgrades a logic version.
        @dev It throws a require error if:
            - The new logic is equal to the current logic.
        @param self the current logic version.
        @param newLogic the new logic to set in the logic version.
        @return oldLogic the old logic address.
        @return oldVersion the old version.
        @return newVersion the new version.
     */
    function upgrade(LogicVersion storage self, address newLogic)
        internal
        returns (
            address oldLogic,
            uint256 oldVersion,
            uint256 newVersion
        )
    {
        requireExists(self);
        require(
            self.versions[self.currentVersion] != newLogic,
            "NEW_LOGIC_REQUIRED"
        );
        require(newLogic.isContract(), "LOGIC_MUST_BE_CONTRACT");
        oldLogic = self.versions[self.currentVersion];
        oldVersion = self.currentVersion;
        newVersion = self.latestVersion.add(1);

        self.currentVersion = newVersion;
        self.latestVersion = newVersion;
        self.versions[newVersion] = newLogic;
    }
}
","keccak256":"0x1d3ce95637a20616ce867dc76775063e12781aac5a00fd51a752b8621c36a167","license":"MIT"},"contracts/util/NumbersLib.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**
 * @dev Utility library for uint256 numbers
 *
 * @author develop@teller.finance
 */
library NumbersLib {
    using SafeMath for uint256;

    /**
        @dev It represents 100% with 2 decimal places.
     */
    function ONE_HUNDRED_PERCENT() internal pure returns (uint256) {
        return 10000;
    }

    /**
        @notice Returns the positive difference value of a number to another number
        @param self The number to return the difference value for
        @param other The other number to calucualte the difference against
        @return uint256 The difference value
     */
    function diff(uint256 self, uint256 other) internal pure returns (uint256) {
        return other > self ? other.sub(self) : self.sub(other);
    }

    /**
        @notice Returns the positive percentage difference of a value to 100%
        @param self The number to return the percentage difference for
        @return uint256 The percentage difference value
     */
    function diffOneHundredPercent(uint256 self)
        internal
        pure
        returns (uint256)
    {
        return diff(self, ONE_HUNDRED_PERCENT());
    }

    /**
     * @notice Returns a percentage value of a number.
     * @param self The number to get a percentage of.
     * @param percentage The percentage value to calculate with 2 decimal places (10000 = 100%).
     */
    function percent(uint256 self, uint256 percentage)
        internal
        pure
        returns (uint256)
    {
        return self.mul(percentage).div(ONE_HUNDRED_PERCENT());
    }

    function percent(int256 self, uint256 percentage)
        internal
        pure
        returns (int256)
    {
        return (self * int256(percentage)) / int256(ONE_HUNDRED_PERCENT());
    }

    function abs(int256 self) internal pure returns (uint256) {
        return self >= 0 ? uint256(self) : uint256(-1 * self);
    }

    /**
     * @notice Returns a ratio of 2 numbers.
     * @param self The number to get a ratio of.
     * @param num The number to calculate the ratio for.
     * @return Ratio of 2 numbers with 2 decimal places (10000 = 100%).
     */
    function ratioOf(uint256 self, uint256 num)
        internal
        pure
        returns (uint256)
    {
        return self.mul(ONE_HUNDRED_PERCENT()).div(num);
    }
}
