pragma experimental ABIEncoderV2;

// Interfaces
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/IERC20.sol";
import "./LendingPoolInterface.sol";

/**
    @notice This contract acts as an interface for the Teller token (TToken).

    @author develop@teller.finance
 */
contract ITToken is IERC20 {
    /**
     * @notice The LendingPool linked to this Teller Token.
     */
    function lendingPool() external view returns (LendingPoolInterface);

    /**
     * @notice The token that is the underlying assets for this Teller token.
     */
    function underlying() external view returns (address);

    /**
     * @notice Increase account supply of specified token amount.
     * @param account The account to mint tokens to.
     * @param amount The amount of tokens to mint.
     * @return true if successful.
     */
    function mint(address account, uint256 amount) external returns (bool);

    /**
     * @notice Reduce account supply of specified token amount.
     * @param account The account to burn tokens from.
     * @param amount The amount of tokens to burn.
     * @return true if successful.
     */
    function burn(address account, uint256 amount) external returns (bool);

    /**
     * @param lendingPoolAddress the address of the lending pool this token is linked to. It is only used to add it as a minter.
     */
    function initialize(address lendingPoolAddress) external;
}
","keccak256":"0x76df326806616879ae9f06ce801dc9e800eba74a50b735bbb295618fe1c5d670"},"contracts/interfaces/LendingPoolInterface.sol":{"content":"pragma solidity 0.5.17;
pragma experimental ABIEncoderV2;

// Utils
import "@openzeppelin/contracts-ethereum-package/contracts/token/ERC20/ERC20Detailed.sol";

// Interfaces
import "./IMarketRegistry.sol";
import "./ITToken.sol";
import "../providers/compound/CErc20Interface.sol";

/**
    @notice This interface defines the functions for a lending pool that holds all of the tokens
    that lenders transfer into the protocol.

    @author develop@teller.finance
 */
interface LendingPoolInterface {
    /**
        @notice It allows users to deposit tokens into the pool.
        @dev the user must call ERC20.approve function previously.
        @param amount of tokens to deposit in the pool.
    */
    function deposit(uint256 amount) external;

    /**
        @notice It allows any tToken holder to burn their tToken tokens and withdraw their tokens.
        @param amount of tokens to withdraw.
        @dev It throws a require error if the contract hasn't enough tokens balance.
        @dev It throws a require error if the holder hasn't enough tToken balance.
     */
    function withdraw(uint256 amount) external;

    /**
        @notice It allows a borrower repaying their loan.
        @dev This function can be called ONLY by the LoanManager contract.
        @dev It requires a ERC20.approve call before calling it.
        @dev It throws a require error if borrower called ERC20.approve function before calling it.
        @param principalAmount amount of tokens towards the principal.
        @param interestAmount amount of tokens towards the interest.
        @param borrower address that is repaying the loan.
     */
    function repay(
        uint256 principalAmount,
        uint256 interestAmount,
        address borrower
    ) external;

    /**
        @notice Once the loan is created, it transfers the amount of tokens to the borrower.
        @param amount of tokens to transfer.
        @param borrower address which will receive the tokens.
        @dev This function only can be invoked by the LoanManager implementation.
        @dev It throws a require error if current ERC20 balance isn't enough to transfer the tokens.
     */
    function createLoan(uint256 amount, address borrower) external;

    /**
        @notice It gets the lending token address.
        @return the ERC20 lending token address.
    */
    function lendingToken() external view returns (ERC20Detailed);

    /**
        @notice It initializes the contract state variables.
        @param aMarketRegistry the MarketRegistry contract.
        @param aLendingToken The underlying token that is used for lending.
        @param aTToken the Teller token to link to the lending pool.
        @param settingsAddress Settings contract address.
        @dev It throws a require error if the contract is already initialized.
     */
    function initialize(
        IMarketRegistry aMarketRegistry,
        address aLendingToken,
        address aTToken,
        address settingsAddress
    ) external;

    /**
        @notice It gets the cToken address.
        @return the cToken address.
    */
    function cToken() external view returns (CErc20Interface);

    /**
        @notice It gets the tToken address.
        @return the tToken address.
    */
    function tToken() external view returns (ITToken);

    /**
        @notice It returns the balance of underlying tokens a lender owns with the amount
        of TTokens owned and the current exchange rate.
        @return a lender's balance of the underlying token in the pool.
     */
    function balanceOfUnderlying(address lender) external returns (uint256);

    /**
        @notice Returns the total amount of interest earned by a lender.
        @dev This value includes already claimed + unclaimed interest earned.
        @return total interest earned by lender.
     */
    function getLenderInterestEarned(address lender) external returns (uint256);

    /**
        @notice Returns the amount of claimable interest a lender has earned.
        @return claimable interest value.
     */
    function getClaimableInterestEarned(address lender)
        external
        returns (uint256);

    /**
        @notice Returns the total amount of interest the pool has earned from repaying loans.
        @return total interest earned from loans.
     */
    function totalInterestEarned() external view returns (uint256);

    /**
        @notice It calculates the market state values across all markets.
        @return values that represent the global state across all markets.
     */
    function getMarketState()
        external
        view
        returns (
            uint256 totalSupplied,
            uint256 totalBorrowed,
            uint256 totalRepaid,
            uint256 totalOnLoan
        );

    /**
        @notice It gets the debt-to-supply (DtS) ratio for a given market, including a new loan amount.
        @notice The formula to calculate DtS ratio (including a new loan amount) is:

            DtS = (SUM(total borrowed) - SUM(total repaid) + NewLoanAmount) / SUM(total supplied)

        @notice The value has 2 decimal places.
            Example:
                100 => 1%
        @param loanAmount a new loan amount to consider in the ratio.
        @return the debt-to-supply ratio value.
     */
    function getDebtRatioFor(uint256 loanAmount)
        external
        view
        returns (uint256);

    /**
        @notice This event is emitted when an user deposits tokens into the pool.
        @param sender address.
        @param amount of tokens.
     */
    event TokenDeposited(
        address indexed sender,
        uint256 amount,
        uint256 tTokenAmount
    );

    /**
        @notice This event is emitted when an user withdraws tokens from the pool.
        @param sender address that withdrew the tokens.
        @param amount of tokens.
     */
    event TokenWithdrawn(
        address indexed sender,
        uint256 amount,
        uint256 tTokenAmount
    );

    /**
        @notice This event is emitted when an borrower repaid a loan.
        @param borrower address.
        @param amount of tokens.
     */
    event TokenRepaid(address indexed borrower, uint256 amount);

    /**
        @notice This event is emitted when an lender withdraws interests.
        @param lender address.
        @param amount of tokens.
     */
    event InterestWithdrawn(address indexed lender, uint256 amount);

    /**
        @notice This event is emitted when the interest validator is updated.
        @param sender account that sends the transaction.
        @param oldInterestValidator the old validator address.
        @param newInterestValidator the new validator address.
     */
    event InterestValidatorUpdated(
        address indexed sender,
        address indexed oldInterestValidator,
        address indexed newInterestValidator
    );
}
","keccak256":"0x39b1adda7725d55a0e96be437f39508fd9cdb9c68bcd0726eba222e03b0b0d4f"},"contracts/interfaces/LogicVersionsRegistryInterface.sol":{"content":"pragma solidity 0.5.17;
pragma experimental ABIEncoderV2;

// Commons
import "../util/LogicVersionLib.sol";
import "../util/TellerCommon.sol";

/**
    @notice It defines all the functions to manage the logic contract versions.

    @author develop@teller.finance
 */
interface LogicVersionsRegistryInterface {
    /**
        @notice This event is emitted when a new logic version is created.
        @param logicName new logic name.
        @param logic address where the logic is.
        @param version initial version for the logic address.
     */
    event LogicVersionCreated(
        bytes32 indexed logicName,
        address indexed logic,
        uint256 version
    );

    /**
        @notice This event is emitted when a logic version is rollbacked.
        @param logicName the logic name.
        @param oldLogic the old logic address.
        @param newLogic the new (or previous) logic address.
        @param oldVersion the old version.
        @param newVersion the new (previous) version.
     */
    event LogicVersionRollbacked(
        bytes32 indexed logicName,
        address oldLogic,
        address newLogic,
        uint256 oldVersion,
        uint256 newVersion
    );

    /**
        @notice This event is emitted when a new logic version is upgraded.
        @param logicName new logic name.
        @param oldLogic the old logic address.
        @param newLogic the new logic address.
        @param oldVersion the old version.
        @param newVersion the new version.
     */
    event LogicVersionUpgraded(
        bytes32 indexed logicName,
        address oldLogic,
        address newLogic,
        uint256 oldVersion,
        uint256 newVersion
    );

    /** External Functions */

    /**
        @notice It creates multiple logic versions.
        @param newLogicVersions lists of the new logic versions to create.
     */
    function createLogicVersions(
        TellerCommon.CreateLogicVersionRequest[] calldata newLogicVersions
    ) external;

    /**
        @notice It upgrades multiple logic addresses.
        @param newLogicVersions lists of the new logic versions to create.
     */
    function upgradeLogicVersions(
        TellerCommon.UpgradeLogicVersionRequest[] calldata newLogicVersions
    ) external;

    /**
        @notice It upgrades a logic version given a logic name.
        @param logicName logic name to upgrade.
        @param newLogic the new logic address to set.
        @param proxy The (optional) DynamicUpgradeable proxy address to attempt to directly upgrade.
     */
    function upgradeLogicVersion(
        bytes32 logicName,
        address newLogic,
        address proxy
    ) external;

    /**
        @notice It rollbacks a logic to a previous version.
        @param logicName logic name to rollback.
        @param previousVersion the previous version to be used.
     */
    function rollbackLogicVersion(bytes32 logicName, uint256 previousVersion)
        external;

    /**
        @notice It gets the current logic version for a given logic name.
        @param logicName to get.
        @return the current logic version.
     */
    function getLogicVersion(bytes32 logicName)
        external
        view
        returns (
            uint256 currentVersion,
            uint256 latestVersion,
            address logic
        );

    /**
        @notice It tests whether a logic name is already configured.
        @param logicName logic name to test.
        @return true if the logic version is already configured. Otherwise it returns false.
     */
    function hasLogicVersion(bytes32 logicName) external view returns (bool);

    /**
        @notice It initializes this logic versions registry contract instance.
        @param aOwner address of the owner of the registry.
        @param initialLogicVersions lists of the new logic versions to create.
     */
    function initialize(
        address aOwner,
        TellerCommon.CreateLogicVersionRequest[] calldata initialLogicVersions
    ) external;
}
","keccak256":"0x7e5eca5bb178277eefa1b09e8a53741fbdddf486ad9805f040ef87aa5b3d0fb0"},"contracts/interfaces/SettingsInterface.sol":{"content":"pragma solidity 0.5.17;
pragma experimental ABIEncoderV2;

import "../util/PlatformSettingsLib.sol";
import "./IDappRegistry.sol";
import "../providers/chainlink/IChainlinkAggregator.sol";
import "../interfaces/AssetSettingsInterface.sol";
import "./IMarketFactory.sol";

/**
    @notice This interface defines all function to manage the platform configuration.

    @author develop@teller.finance
 */
interface SettingsInterface {
    /**
     * @dev Emitted when the pause is triggered by a pauser (`account`).
     */
    event Paused(address account);

    /**
     * @dev Emitted when the pause is lifted by a pauser (`account`).
     */
    event Unpaused(address account);

    event PauserAdded(address indexed account);
    event PauserRemoved(address indexed account);

    /**
        @notice This event is emitted when a new platform setting is created.
        @param settingName new setting name.
        @param sender address that created it.
        @param value value for the new setting.
     */
    event PlatformSettingCreated(
        bytes32 indexed settingName,
        address indexed sender,
        uint256 value,
        uint256 minValue,
        uint256 maxValue
    );

    /**
        @notice This event is emitted when a current platform setting is removed.
        @param settingName setting name removed.
        @param sender address that removed it.
     */
    event PlatformSettingRemoved(
        bytes32 indexed settingName,
        uint256 lastValue,
        address indexed sender
    );

    /**
        @notice This event is emitted when a platform setting is updated.
        @param settingName settings name updated.
        @param sender address that updated it.
        @param oldValue old value for the setting.
        @param newValue new value for the setting.
     */
    event PlatformSettingUpdated(
        bytes32 indexed settingName,
        address indexed sender,
        uint256 oldValue,
        uint256 newValue
    );

    /**
        @notice This event is emitted when a lending pool is paused.
        @param account address that paused the lending pool.
        @param lendingPoolAddress lending pool address which was paused.
     */
    event LendingPoolPaused(
        address indexed account,
        address indexed lendingPoolAddress
    );

    /**
        @notice This event is emitted when a lending pool is unpaused.
        @param account address that paused the lending pool.
        @param lendingPoolAddress lending pool address which was unpaused.
     */
    event LendingPoolUnpaused(
        address indexed account,
        address indexed lendingPoolAddress
    );

    /**
        @notice This event is emitted when the platform restriction is switched
        @param restriction Boolean representing the state of the restriction
        @param pauser address of the pauser flipping the switch
    */
    event PlatformRestricted(bool restriction, address indexed pauser);

    /**
        @notice This event is emitted when an address is given authorization
        @param user The address being authorized
        @param pauser address of the pauser adding the address
    */
    event AuthorizationGranted(address indexed user, address indexed pauser);

    /**
        @notice This event is emitted when an address has authorization revoked
        @param user The address being revoked
        @param pauser address of the pauser removing the address
    */
    event AuthorizationRevoked(address indexed user, address indexed pauser);

    /**
        @notice It creates a new platform setting given a setting name, value, min and max values.
        @param settingName setting name to create.
        @param value the initial value for the given setting name.
        @param minValue the min value for the setting.
        @param maxValue the max value for the setting.
     */
    function createPlatformSetting(
        bytes32 settingName,
        uint256 value,
        uint256 minValue,
        uint256 maxValue
    ) external;

    function assetSettings() external view returns (AssetSettingsInterface);

    /**
     * @notice It holds the address of a deployed InitializeableDynamicProxy contract.
     * @dev It is used to deploy a new proxy contract with minimal gas cost using the logic in the Factory contract.
     */
    function initDynamicProxyLogic() external view returns (address);

    /**
        @notice It updates an existent platform setting given a setting name.
        @notice It only allows to update the value (not the min or max values).
        @notice In case you need to update the min or max values, you need to remove it, and create it again.
        @param settingName setting name to update.
        @param newValue the new value to set.
     */
    function updatePlatformSetting(bytes32 settingName, uint256 newValue)
        external;

    /**
        @notice Removes a current platform setting given a setting name.
        @param settingName to remove.
     */
    function removePlatformSetting(bytes32 settingName) external;

    /**
        @notice It gets the current "RequiredSubmissionsPercentage" setting's value
        @return the current value.
     */
    function getRequiredSubmissionsPercentageValue()
        external
        view
        returns (uint256 value);

    /**
        @notice It gets the current "MaximumTolerance" setting's value
        @return the current value.
     */
    function getMaximumToleranceValue() external view returns (uint256 value);

    /**
        @notice It gets the current "ResponseExpiryLength" setting's value
        @return the current value.
     */
    function getResponseExpiryLengthValue()
        external
        view
        returns (uint256 value);

    /**
        @notice It gets the current "SafetyInterval" setting's value
        @return the current value.
     */
    function getSafetyIntervalValue() external view returns (uint256 value);

    /**
        @notice It gets the current "TermsExpiryTime" setting's value
        @return the current value.
     */
    function getTermsExpiryTimeValue() external view returns (uint256 value);

    /**
        @notice It gets the current "LiquidateEthPrice" setting's value
        @return the current value.
     */
    function getLiquidateEthPriceValue() external view returns (uint256 value);

    /**
        @notice It gets the current "MaximumLoanDuration" setting's value
        @return the current value.
     */
    function getMaximumLoanDurationValue()
        external
        view
        returns (uint256 value);

    /**
        @notice It gets the current "RequestLoanTermsRateLimit" setting's value
        @return the current value.
     */
    function getRequestLoanTermsRateLimitValue()
        external
        view
        returns (uint256 value);

    /**
        @notice It gets the current "CollateralBuffer" setting's value
        @return the current value.
     */
    function getCollateralBufferValue() external view returns (uint256 value);

    /**
        @notice It gets the current "OverCollateralizedBuffer" setting's value
        @return the current value.
     */
    function getOverCollateralizedBufferValue()
        external
        view
        returns (uint256 value);

    /**
        @notice It tests whether a setting name is already configured.
        @param settingName setting name to test.
        @return true if the setting is already configured. Otherwise it returns false.
     */
    function hasPlatformSetting(bytes32 settingName)
        external
        view
        returns (bool);

    /**
        @notice It gets whether the platform is paused or not.
        @return true if platform is paused. Otherwise it returns false.
     */
    function isPaused() external view returns (bool);

    /**
        @notice It gets whether a lending pool is paused or not.
        @param lendingPoolAddress lending pool address to test.
        @return true if the lending pool is paused. Otherwise it returns false.
     */
    function lendingPoolPaused(address lendingPoolAddress)
        external
        view
        returns (bool);

    /**
        @notice It pauses a specific lending pool.
        @param lendingPoolAddress lending pool address to pause.
     */
    function pauseLendingPool(address lendingPoolAddress) external;

    /**
        @notice It unpauses a specific lending pool.
        @param lendingPoolAddress lending pool address to unpause.
     */
    function unpauseLendingPool(address lendingPoolAddress) external;

    function platformSettings(bytes32)
        external
        view
        returns (PlatformSettingsLib.PlatformSetting memory);

    /**
        @notice Tests whether an account has the pauser role.
        @param account account to test.
        @return true if account has the pauser role. Otherwise it returns false.
     */
    function isPauser(address account) external view returns (bool);

    /**
        @notice Requires an account to have the pauser role.
        @param account account to test.
     */
    function requirePauserRole(address account) external view;

    /**
        @notice Restricts the use of the Teller protocol to authorized wallet addresses only
        @param restriction Bool turning the resitriction on or off
     */
    function restrictPlatform(bool restriction) external;

    /**
        @notice Adds a wallet address to the list of authorized wallets
        @param addressToAdd The wallet address of the user being authorized
     */
    function addAuthorizedAddress(address addressToAdd) external;

    /**
        @notice Adds a list of wallet addresses to the list of authorized wallets
        @param addressesToAdd The list of wallet addresses being authorized
     */
    function addAuthorizedAddressList(address[] calldata addressesToAdd)
        external;

    function addEscrowAuthorized(address escrowAddress) external;

    /**
        @notice Returns whether the platform is restricted or not
        @return bool True if the platform is restricted, false if not
     */
    function isPlatformRestricted() external view returns (bool);

    /**
        @notice Tests whether an account has authorization
        @param account The account address to check for
        @return True if account has authorization, false if it does not
     */
    function hasAuthorization(address account) external view returns (bool);

    /**
        @notice Requires an account to have platform authorization.
        @param account account to test.
     */
    function requireAuthorization(address account) external view;

    /**
        @notice Removes a wallet address from the list of authorized wallets
        @param addressToRemove The wallet address of the user being unauthorized
     */
    function removeAuthorizedAddress(address addressToRemove) external;

    /**
        @notice It is the global instance of the DappRegistry contract.
     */
    function dappRegistry() external view returns (IDappRegistry);

    /**
        @notice It is the global instance of the ChainlinkAggregator contract.
     */
    function chainlinkAggregator() external view returns (IChainlinkAggregator);

    /**
        @notice It is the global instance of the MarketFactory contract.
     */
    function marketFactory() external view returns (IMarketFactory);

    /**
        @notice Gets the cToken address for a given asset address.
        @param assetAddress token address.
        @return the cToken address for a given asset address.
     */
    function getCTokenAddress(address assetAddress)
        external
        view
        returns (address);

    /**
        @notice It initializes this settings contract instance.
        @param wethTokenAddress canonical WETH token address.
        @param cethTokenAddress compound CETH token address.
        @param initDynamicProxyAddress Address of a deployed InitializeableDynamicProxy contract.
     */
    function initialize(
        address wethTokenAddress,
        address cethTokenAddress,
        address initDynamicProxyAddress
    ) external;

    /**
        @notice It gets the ETH address used in the platform.
        @return the ETH address used in the platform.
     */
    function ETH_ADDRESS() external view returns (address);

    /**
        @notice It gets the canonical WETH address used in the platform.
        @return the canonical WETH address used in the platform.
     */
    function WETH_ADDRESS() external view returns (address);

    /**
        @notice It gets the canonical CETH address used in the platform.
        @return the canonical CETH address used in the platform.
     */
    function CETH_ADDRESS() external view returns (address);
}
